    //    this.setState(prevState => ({
    //         items: [...prevState.items, newItem],
    //    }));

    //    e.target.reset();
    // }

    render() {
        return (
            <>
                <ListWrapper items={this.state.items}/>
                <Form submitFn={this.addItem}/>
            </>
        )
    }
}

export default App;
