import React from 'react';
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Title from "../Title/Title";
import Radio from "./FormRadio";
import AppContext from "../../context";

const types = {
    Links: 'Links',
    Articles: 'Articles',
    Notes: 'Notes',
}
const description = {
    Links: 'Link',
    Articles: 'Article',
    Notes: 'Note',
}

class Form extends React.Component {
    state = {
        activeOption: types.Links,
    }
    handle = (type) => {
        this.setState({
            activeOption: type,
        })
    }
    handleItem = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    }
   render(){

    const {activeOption} = this.state;
    
       return (
        <AppContext.Consumer>
        {(context) => (
             <div className={styles.wrapper}>
             <Title>Add new {description[activeOption]}</Title>
             <form className={styles.form} onSubmit={context.addItem}>

                 <div>
                     <Radio
                         id={types.Links}
                         type="radio"
                         checked={activeOption === types.Links}
                         changeFn={() => this.handle(types.Links)}
                     >Links</Radio>
                     <Radio
                         id={types.Articles}
                         type="radio"
                         checked={activeOption === types.Articles}
                         changeFn={() => this.handle(types.Articles)}
                     >Articles</Radio>
                     <Radio
                         id={types.Notes}
                         type="radio"
                         checked={activeOption === types.Notes}
                         changeFn={() => this.handle(types.Notes)}
                     >Notes</Radio>
                 </div>

                 <Input 
                     name="title" 
                     label={activeOption !== types.Links ? "Title" : "Name" } 
                     onChange={this.handleInput}
                     value={this.state.title}
                 />

                 {activeOption !== types.Notes ? 
                     (<Input 
                         name="link" 
                         label="Link"
                         onChange={this.handleInput}
                         value={this.state.link}
                     />) 
                 : null}

                 {activeOption === types.Links ? 
                     (<Input 
                         name="image" 
                         label="Image" 
                         onChange={this.handleInput}
                         value={this.state.image}
                     />) 
                 : null}
                 
                 <Input 
                 name="description"
                     label="Description" 
                     tag="textarea"
                     onChange={this.handleInput}
                     value={this.state.description}
                 />
                 <Button>Add Item</Button>
             </form>
         </div>
        )}
    </AppContext.Consumer>
       )
   }
};

export default Form;
