import React from 'react';
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Title from "../Title/Title";
import Radio from "./FormRadio";
import AppContext from "../../context";

const types = {
    link: "link",
    article: "article",
    note: "note",
  };

const descriptions = {
  link: "favorite link",
  article: "Article",
  note: "Note",
};

class Form extends React.Component {
    state = {
        type: types.link,
        title: "",
        link: "",
        image: "",
        description: "",
      };
    handle = type => {
        this.setState({
          type: type,
        });
      };

    handleItem = e => {
        this.setState({
          [e.target.name]: e.target.value,
        });
        
      };
   render(){

    const {type} = this.state;
    
       return (
            <AppContext.Consumer>
                {(context) => (
                    <div className={styles.wrapper}>
                    <Title>Add New {descriptions[type]}</Title>
                    <form 
                        autoComplete="off" 
                        className={styles.form}
                        onSubmit={(e) => context.addItem(e , 
                            this.state)} >
                        <div>
                        <Radio 
                            id={types.Link} 
                            type="radio"
                            checked={type === types.Link} 
                            changeFn={() => this.handle(types.Link)}
                        > Links </Radio>
                        <Radio
                            id={types.Article}
                            type="radio"
                            checked={type === types.Article}
                            changeFn={() => this.handle(types.Article)}
                        > Articles </Radio>
                        <Radio
                            id={types.Note}
                            type="radio"
                            checked={type === types.Note}
                            changeFn={() => this.handle(types.Note)}
                        > Notes </Radio>
                        </div>
                        
                        <Input 
                            name="title" 
                            label={type !== types.Link ? "Title" : "Name"} 
                            maxLength={40}
                            onChange={this.handleItem}
                            value={this.state.title}
                        />

                        {type !== types.Note ? 
                            (<Input 
                                name="link" 
                                label="Link" 
                                onChange={this.handleItem}
                                value={this.state.link}
                            />) 
                        : null}

                        {type === types.Link ? 
                            (<Input 
                                name="image" 
                                label="Image" 
                                onChange={this.handleItem}
                                value={this.state.image}
                            />) 
                        : null}

                        <Input 
                            name="description"
                            label="Description" 
                            tag="textarea"
                            onChange={this.handleItem}
                            value={this.state.description}
                        />

                        <Button>Dodaj item</Button>
                    </form>
                </div>
                )}
            </AppContext.Consumer>
       )
   }
};

export default Form;
