import React from 'react';
import PropTypes from 'prop-types';
import styles from './ListItem.module.scss';
import Button from "../Button/Button";

const ListItem = ({
    image,
    name,
    description,
    link
}) => {

    const ImageTag = image ? "img" : "div";

    return (
        <li className={styles.ListItem__wrapper}>
            <ImageTag
                className={image ? styles.ListItem__img : styles.ListItem__imgNone}
                src={image}
                alt={name}
            />
            <div>
                
                <p className={styles.ListItem__description}>{description}</p>
                <Button href={link}>go to link</Button>
            </div>
        </li>
    )
};
ListItem.PropTypes = {
    image: PropTypes.string,
    name: PropTypes.string.isRequired,
    description: PropTypes.string,
    link: PropTypes.string.isRequired,
    
};
ListItem.defaultProps = {
    image: null,
    description: 'Dili dali bomba',
    
}

export default ListItem;
