import React from 'react';
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Title from "../Title/Title";
import Radio from "./FormRadio";
import AppContext from "../../context";

const types = {
    Link: 'Link',
    Article: 'Article',
    Note: 'Note',
}
const descriptions = {
    Link: 'Link',
    Article: 'Article',
    Note: 'Note',
}

class Form extends React.Component {
    state = {
        activeOption: types.Link,
        title: '',
        link: '',
        image: '',
        description: '',
    }
    handle = (type) => {
        this.setState({
            activeOption: type,
        })
    }

    handleItem = e => {
        this.setState({
            [e.target.name]: e.target.value,
        });
        console.log(`
            title: ${this.state.title},
            link: ${this.state.link},
            image: ${this.state.image},
            description: ${this.state.description},
        `);
    }
   render(){

    const {activeOption} = this.state;
    
       return (
            <AppContext.Consumer>
                {(context) => (
                    <div className={styles.wrapper}>
                    <Title>Add New {descriptions[activeOption]}</Title>
                    <form 
                        autoComplete="off" 
                        onSubmit={(e) => context.addItem(e, this.state)} 
                        className={styles.form}>
                        <div>
                        <Radio 
                            id={types.Link} 
                            type="radio"
                            checked={activeOption === types.Link} 
                            changeFn={() => this.handle(types.Link)}
                        > Links </Radio>
                        <Radio
                            id={types.Article}
                            type="radio"
                            checked={activeOption === types.Article}
                            changeFn={() => this.handle(types.Article)}
                        > Articles </Radio>
                        <Radio
                            id={types.Note}
                            type="radio"
                            checked={activeOption === types.Note}
                            changeFn={() => this.handle(types.Note)}
                        > Notes </Radio>
                        </div>
                        
                        <Input 
                            name="title" 
                            label={activeOption !== types.Link? "Title" : "Name"} 
                            maxLength={40}
                            onChange={this.handleItem}
                            value={this.state.title}
                        />

                        {activeOption !== types.Note ? 
                            (<Input 
                                name="link" 
                                label="Link" 
                                onChange={this.handleItem}
                                value={this.state.link}
                            />) 
                        : null}

                        {activeOption === types.Link ? 
                            (<Input 
                                name="image" 
                                label="Image" 
                                onChange={this.handleItem}
                                value={this.state.image}
                            />) 
                        : null}

                        <Input 
                            name="description"
                            label="Description" 
                            tag="textarea"
                            onChange={this.handleItem}
                            value={this.state.description}
                        />

                        <Button>Dodaj item</Button>
                    </form>
                </div>
                )}
            </AppContext.Consumer>
       )
   }
};

export default Form;
