import React from 'react';
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Title from "../Title/Title";
import Radio from "./FormRadio";
import AppContext from '../../context';

const types = {
    Links: 'Links',
    Articles: 'Articles',
    Notes: 'Notes',
}
const description = {
    Links: 'Link',
    Articles: 'Article',
    Notes: 'Note',
}

class Form extends React.Component {
    state = {
        activeOption: types.Links,
        title: '',
        link: '',
        image: '',
        description: '',
    }
    handle = (type) => {
        this.setState({
            activeOption: type,
        })
    }
    handleInput = e => {
        console.log(e.target.name);
    }
   render(){

    const {activeOption} = this.state;
    
       return (
        <AppContext.Consumer>
            {(context) => (
                <div className={styles.wrapper}>
                <Title>Add New {description[activeOption]}</Title>
                <form autoComplete="off" onSubmit={context.addItem} className={styles.form}>
                    <div>
                    <Radio 
                        id={types.Links}
                        type="radio"
                        checked={activeOption === types.Links} 
                        changeFn={() => this.handle(types.Links)}
                    > Links </Radio>
                    <Radio
                        id={types.Articles}
                        type="radio"
                        checked={activeOption === types.Articles}
                        changeFn={() => this.handle(types.Articles)}
                    > Articles </Radio>
                    <Radio
                        id={types.Notes}
                        type="radio"
                        checked={activeOption === types.Notes}
                        changeFn={() => this.handle(types.Notes)}
                    > Notes </Radio>
                    </div>
                    
                    <Input 
                        onChange={this.handleInput} 
                        name="title" label={activeOption !== types.Links ? "Title" : "Name"} 
                        maxLength={40} 
                    />

                    {activeOption !== types.Notes ? 
                        (<Input 
                            onChange={this.handleInput} 
                            name="link" 
                            label="Link" 
                        />) 
                    : null}

                    {activeOption === types.Links ? 
                        (<Input 
                            onChange={this.handleInput} 
                            name="image" 
                            label="Image" 
                        />)
                    : null}
                    
                    <Input 
                        tag="textarea" 
                        onChange={this.handleInput} 
                        name="description" 
                        label="Description" 
                    />
                    
                    <Button>Dodaj item</Button>
                </form>
            </div>
            )}
        </AppContext.Consumer>
       )
   }
};

export default Form;
