import React from 'react';
import styles from "./Form.module.scss";
import Input from "../Input/Input";
import Button from "../Button/Button";
import Title from "../Title/Title";
import Radio from "./FormRadio";

const types = {
    Links: 'Links',
    Articles: 'Articles',
    Notes: 'Notes',
}
const description = {
    Links: 'Link',
    Articles: 'Article',
    Notes: 'Note',
}
class Form extends React.Component {
    state = {
        activeOption: types.Links,
    }
    handle = (type) => {
        this.setState({
            activeOption: type,
        })
    }
   render(){
       return (
        <div className={styles.wrapper}>
        <Title>Add New {description[this.state.activeOption]}</Title>
        <form autoComplete="off" onSubmit={this.props.submitFn} className={styles.form}>
            <div>
            <Radio 
                id={types.Links}
                type="radio"
                checked={this.state.activeOption === types.Links}
                changeFn={() => this.handle(types.Links)}
            > Links </Radio>
           

            <Radio
                id={types.Articles}
                type="radio"
                checked={this.state.activeOption === types.Articles}
                changeFn={() => this.handle(types.Articles)}
            > Articles </Radio>
            

            <Radio
                id={types.Notes}
                type="radio"
                checked={this.state.activeOption === types.Notes}
                changeFn={() => this.handle(types.Notes)}
            > Notes </Radio>
            </div>
            
            <Input 
                name="name"
                label="Name"
                maxLength={40}
            />
            <Input 
                name="link"
                label="Link"
            />
            <Input 
                name="image"
                label="Image"
            />
            <Input 
                tag="textarea"
                name="description"
                label="Description"
            />
            {/* <div className={styles.form__item}>
                <input
                    name="name"
                    id="name"
                    type="text"
                    placeholder=" "
                    maxLength="30"
                    required
                />
                <label hrmlFor="name">name</label>
            </div>
